body.script:
// Sidebar collapse
document.addEventListener("DOMContentLoaded", function () {
  document.querySelectorAll(".series-title").forEach(t => {
    t.addEventListener("click", function () {
      const ul = this.querySelector("ul");
      if (!ul) return;
      const state = ul.style.display === "block";
      document.querySelectorAll(".series-list ul").forEach(x => x.style.display = "none");
      ul.style.display = state ? "none" : "block";
    });
  });
});

// Auto Table of Contents
document.addEventListener("DOMContentLoaded", function () {
  const area = document.getElementById("bb-content");
  const toc = document.getElementById("toc-list");
  if (!area || !toc) return;

  area.querySelectorAll("h2, h3").forEach((h, i) => {
    const id = "sec-" + i;
    h.setAttribute("id", id);
    const li = document.createElement("li");
    li.style.marginLeft = h.tagName === "H3" ? "20px" : "0";
    li.innerHTML = `${h.textContent}`;
    toc.appendChild(li);
  });
});

// Smooth scroll
document.addEventListener("click", function (e) {
  if (e.target.tagName.toLowerCase() === "a" && e.target.getAttribute("href").startsWith("#")) {
    e.preventDefault();
    const el = document.querySelector(e.target.getAttribute("href"));
    if (el) window.scrollTo({ top: el.offsetTop - 140, behavior: "smooth" });
  }
});

function loadTheme(){ const m = localStorage.getItem("bb-theme") || "dark"; if(m==="light") document.documentElement.classList.add("light"); }
document.getElementById("theme-toggle").onclick = function () {
  document.documentElement.classList.toggle("light");
  localStorage.setItem("bb-theme", document.documentElement.classList.contains("light") ? "light" : "dark");
};
loadTheme();

document.addEventListener("DOMContentLoaded", function () {
  const p = document.querySelector(".post-body");
  if (!p) return;
  const sig = document.createElement("div");
  sig.innerHTML = `

BYTE2BUILD

Learn one command, master one concept, grow one byte at a time.
  `;
  p.appendChild(sig);
});
