body.script:
// ------------------------------
// COLLAPSIBLE SIDEBAR
// ------------------------------
document.addEventListener("DOMContentLoaded", function () {
  const titles = document.querySelectorAll(".series-title");

  titles.forEach(title => {
    title.addEventListener("click", function () {
      const content = this.querySelector("ul");
      if (!content) return;

      const isOpen = content.style.display === "block";

      document.querySelectorAll(".series-list ul").forEach(u => {
        u.style.display = "none";
      });

      content.style.display = isOpen ? "none" : "block";
    });
  });
});


// ------------------------------
// AUTO TABLE OF CONTENTS
// ------------------------------
document.addEventListener("DOMContentLoaded", function () {
  const contentArea = document.getElementById("bb-content");
  const tocList = document.getElementById("toc-list");

  if (!contentArea || !tocList) return;

  const headers = contentArea.querySelectorAll("h2, h3");

  headers.forEach((header, index) => {
    const id = "section-" + index;
    header.setAttribute("id", id);

    const li = document.createElement("li");
    li.style.marginLeft = header.tagName === "H3" ? "15px" : "0";

    const a = document.createElement("a");
    a.href = "#" + id;
    a.textContent = header.textContent;

    li.appendChild(a);
    tocList.appendChild(li);
  });
});


// ------------------------------
// SMOOTH SCROLLING
// ------------------------------
document.addEventListener("click", function (e) {
  if (e.target.tagName.toLowerCase() === "a" && e.target.getAttribute("href").startsWith("#")) {
    e.preventDefault();
    const target = document.querySelector(e.target.getAttribute("href"));

    if (target) {
      window.scrollTo({
        top: target.offsetTop - 140,
        behavior: "smooth"
      });
    }
  }
});

function loadTheme() {
  const mode = localStorage.getItem("bb-theme") || "dark";
  if (mode === "light") {
    document.documentElement.classList.add("light");
  }
}

document.getElementById("theme-toggle").onclick = function () {
  document.documentElement.classList.toggle("light");

  if (document.documentElement.classList.contains("light")) {
    localStorage.setItem("bb-theme", "light");
  } else {
    localStorage.setItem("bb-theme", "dark");
  }
};

loadTheme();

document.addEventListener("DOMContentLoaded", function () {
  const post = document.querySelector(".post-body");

  if (!post) return;

  const sig = document.createElement("div");
  sig.className = "signature-box";

  sig.innerHTML = `

BYTE2BUILD

Learn one command, master one concept, grow one byte at a time.
  `;

  post.appendChild(sig);
});
