body.script:
document.addEventListener("DOMContentLoaded", function () {
  const post = document.querySelector(".post-body");
  if (!post) return;

  const box = document.createElement("div");
  box.className = "author-box";
  box.innerHTML = `

Author â€¢ Byte2Build

Learn one command, master one concept, grow one byte at a time.
  `;
  post.appendChild(box);
});

// ------------------------------
// STICKY SIDEBAR COLLAPSE
// ------------------------------
document.addEventListener("DOMContentLoaded", function () {
  const titles = document.querySelectorAll(".series-title");

  titles.forEach(title => {
    title.addEventListener("click", function () {
      const content = this.querySelector("ul");
      if (!content) return;

      const isOpen = content.style.display === "block";

      document.querySelectorAll(".series-list ul").forEach(u => {
        u.style.display = "none";
      });

      content.style.display = isOpen ? "none" : "block";
    });
  });
});


// ------------------------------
// AUTO TABLE OF CONTENTS ENGINE
// ------------------------------
document.addEventListener("DOMContentLoaded", function () {
  const content = document.getElementById("bb-content");
  const tocList = document.getElementById("toc-list");
  if (!content || !tocList) return;

  const headers = content.querySelectorAll("h2, h3");

  headers.forEach((header, index) => {
    const id = "section-" + index;
    header.setAttribute("id", id);

    const li = document.createElement("li");
    li.style.marginLeft = header.tagName === "H3" ? "15px" : "0";
    li.innerHTML = `${header.textContent}`;

    tocList.appendChild(li);
  });
});


// ------------------------------
// SMOOTH SCROLLING ENGINE
// ------------------------------
document.addEventListener("click", function (e) {
  const link = e.target.closest("a");
  if (!link) return;

  const href = link.getAttribute("href");
  if (!href || !href.startsWith("#")) return;

  e.preventDefault();
  const target = document.querySelector(href);

  if (target) {
    window.scrollTo({
      top: target.offsetTop - 140,
      behavior: "smooth"
    });
  }
});


// ------------------------------
// READING PROGRESS BAR ENGINE
// ------------------------------
window.addEventListener("scroll", function () {
  const pageHeight = document.documentElement.scrollHeight - window.innerHeight;
  const scrolled = (window.scrollY / pageHeight) * 100;
  document.getElementById("bb-progress-fill").style.width = scrolled + "%";
});


// ------------------------------
// FLOATING DARK MODE BUTTON ENGINE
// ------------------------------
document.addEventListener("DOMContentLoaded", function () {
  const btn = document.createElement("div");
  btn.id = "darkmode-floating";
  btn.innerHTML = "ðŸŒ™";
  document.body.appendChild(btn);

  btn.onclick = () => {
    document.documentElement.classList.toggle("light");
    localStorage.setItem(
      "bb-theme",
      document.documentElement.classList.contains("light") ? "light" : "dark"
    );
  };

  // Load saved theme
  const mode = localStorage.getItem("bb-theme");
  if (mode === "light") {
    document.documentElement.classList.add("light");
  }
});
